mapper.select:
select * from dress


		and dressname like '%' #{dressname} '%'


		and category=#{category}


		AND price   >=  
		#{minprice}


		AND price <= #{maxprice}


limit #{startRow},#{pageSize}

SELECT count(*) FROM dress


		and dressname like '%' #{dressname} '%'


		and category=#{category}


		AND price >= #{minprice}


		AND price  <= 
		#{maxprice}
